function digest (sequence, enzyme, circularize, removeMarkings) {
  //todo - load enzyme either here or in tests?
  var Digest = clotho.load('Digest');
  var enzymeList = [];
  _.forEach(enzyme, function (enz) {
    enzymeList.push(clotho.query({"name" : enz})[0]);
  });
  return Digest.digest(sequence, enzymeList, circularize, removeMarkings);
}
