function (sharableId) {
 var sharable = clotho.get(sharableId);

  if (!sharable.schema) {
    return null;
  }


  var schemas = [sharable.schema];
  schemas = schemas.concat(clotho.run('clotho.functions.schema.getParents', [sharable.schema]));

  clotho.say(schemas.join(' '));

 if (_.indexOf(schemas, 'org.clothocad.core.datums.Module') > -1 || _.indexOf(schemas, 'org.clothocad.core.datums.Function') > -1) {
    return 'Function';
  } else if (_.indexOf(schemas, 'org.clothocad.core.datums.View') > -1) {
    return 'View';
  } else if (_.indexOf(schemas, 'org.clothocad.core.schema.Schema') > -1) {
  	return 'Schema'
  } else {
    return 'Instance';
  }
}
